$(document).ready(function () {
  var swiper = new Swiper(".mySwiper", {
    // effect: "coverflow",
    grabCursor: true,
    centeredSlides: true,
    loop: true,
    speed: 800,
    slidesPerView: 1,

    pagination: {
      el: ".swiper-pagination",
    },
    navigation: {
      nextEl: ".swiper-button-next",
      prevEl: ".swiper-button-prev",
    },
    on: {
      slideChangeTransitionEnd: function () {
        // updateSlidesVisibility();
      },
      init: function () {
        // updateSlidesVisibility();
      },
    },
    breakpoints: {
      577: {
        slidesPerView: 3,
        coverflowEffect: {
          rotate: 0,
          stretch: 220,
          depth: 350,
          // modifier: 1,
          slideShadows: true,
        },
      },
    },
  });
  function updateSlidesVisibility() {
    slides = document.querySelectorAll(".swiper-slide");
    slides.forEach(function (slide) {
      slide.classList.remove("show-slide");
    });

    CurrentslideNext = document.querySelector(".swiper-slide-next");
    if (CurrentslideNext) {
      index = CurrentslideNext.getAttribute("data-swiper-slide-index");
      console.log(index);
      nextOfNext = CurrentslideNext.nextElementSibling;

      if (nextOfNext) {
        nextOfNext.classList.add("show-slide");
      } else {
        console.log("none");
        slides[0].classList.add("show-slide");
      }

      prevOfNext = CurrentslideNext.previousElementSibling.classList.add("show-slide");
    }
  }
});

